import React from "react";
import {Component} from "react"
import Matargashti from "../audio/Matargashti - Tamasha.mp3"
import Surrender from "../audio/Surrender- Natalie Taylor.mp3"
import Kahani from "../audio/Kahani.mp3"


class MusicPlayer extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            audio:""
        }
    }
    render(){
        if(this.props.playtrack){
            this.setState({
                audio:this.props.playtrack
            })
        }


        return (
            <div>
            <audio controls>
              <source src={audio} type="audio/mp3" ></source>
            </audio>
           
            </div>
        )
    }
}   

export default MusicPlayer
