import React, {Component} from 'react'
import axios from "axios"

class Form extends Component {
constructor(){
    super()
    this.state = {
        firstname:"",
        lastname:""
    }
}

getformData = (e) => {
    this.setState({
        [e.target.name]:e.target.value
    })
}

sendDatatoServer = (e) => {
      e.preventDefault()
     let xhttp = new XMLHttpRequest()
         xhttp.onreadystatechange = () =>{
             if(this.readyState == 4 && this.status == 200){
                 console.log(this.responseText)
             }
         } ;    
         xhttp.open("POST","http://localhost:4000/test",true)
         xhttp.send()
}

    render(){
    return (
        <form>
            <input type="text" name="firstname" onChange={this.getformData} value={this.state.firstname}/>
            <input type="text" name="lastname" onChange={this.getformData} value={this.state.lastname}/>
            <input type="submit" value="submit" onClick={this.sendDatatoServer}/>
            
        </form>
    )
}
}

export default Form
