import React, {Component} from 'react'
import axios from "axios"

class Form extends Component {
constructor(props){
    super(props)
    this.state = {
        playlistname:"",
        cover:"",
        Name:"",
        year:"",
        field:""
    }
}

getformData = (e) => {
    this.setState({
        [e.target.name]:e.target.value,
         Name:this.props.selectedsongs,
         field:e.target.files[0]
    })

