import React, {Component} from 'react'


class Form extends Component {
constructor(){
    super()
    this.state = {
        firstname:"",
        lastname:""
    }
}

getformData = (e) => {
    this.setState({
        [e.target.name]:e.target.value
    })
}

sendDatatoServer = (e) => {
    fetch("https://jsonplaceholder.typicode.com/users",{
        method:"post",
        body:{ name: 'selmon_bhoi', 
        username: '_selmon', 
        email: 'selmonbhoi@gmail.com'}
    })
    fetch("https://jsonplaceholder.typicode.com/users").then(data2 => console.log(data2))
    e.preventDefault()
}

    render(){
    return (
        <form>
            <input type="text" name="firstname" onChange={this.getformData} value={this.state.firstname}/>
            <input type="text" name="lastname" onChange={this.getformData} value={this.state.lastname}/>
            <input type="submit" value="submit" onClick={this.sendDatatoServer}/>
            {this.state.firstname}
            {this.state.lastname}
        </form>
    )
}
}

export default Form
