import logo from "./images/logo.png"
import './App.css';
import React from "react"
import Playlistpage from "./Components/playlistpage"
import Homepage from "./Components/Homepage"


class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      apiresponse:"",
      currentpage:""
    }
  }

  openplaylist = (e) => {
    this.setState({
      currentpage:e.target.className
    })
  }

  ApiCall = () => {
    fetch("http://localhost:4000/test")
    .then(res => res.json())
    .then(response => {
      let songsarray=[];
      response.forEach(val => {
        songsarray.push(val)
        this.setState({
          apiresponse:songsarray
        })
      })
    })
  }
componentDidMount(){
  this.ApiCall()
}


render() {

  return (
    <div>
      <header>
      <ul>
        <li><img src={logo} style={{"height":"50px","width":"50px"}}/></li>
        <li>HOME</li>
        <li>ABOUT US</li>
        <li>PRICING</li>
      </ul>
     {
      this.state.currentpage !== ""?<Playlistpage shoplaylist={this.state.currentpage}/>:<Homepage selected={this.openplaylist}/>
    }
    {this.state.apiresponse}
      </header>
    </div>
  );
}
}

export default App;
