import React from "react";
import {Component} from "react";
import top50 from "../images/top50 global.jpg"
import top50selected from "../images/top50selected.jpg"
import jsondata from "./data.json";
import MusicPlayer from "./songsaudiofiles"


class Playlistpage extends React.Component {
    constructor(props){
        super(props)
        this.state = {
          audio:0,
          songname:"Matargashti - Tamasha",
          i:this.props.shoplaylist.split(".")[0],
          j:this.props.shoplaylist.split(".")[1],
          property:Object.keys(jsondata[this.i]).toString()
        }
    }

    Playsong = (e) => {
     this.setState({
       audio:e.target.value,
       songname:e.target.textContent,
       
     })
    }
 

      render(){
          return (
            <section>
            <div>
            <h1>Homepage</h1>
            <img src={jsondata[this.state.i].Recomended[this.state.j]} style={{"height":"70vh","width":"100%", "objectFit":"cover"}} />
            </div>
            <div>
            <img src={jsondata[this.state.i].Recomended[this.state.j]} style={{"height":"auto","width":"200px", "objectFit":"cover"}} /> 
            <ul>
            {
            jsondata.map((data, i)=>{
              return (<li key={i} value={i} onClick={this.Playsong}>{data.Name}</li>)
            })
            }
            </ul>
            <MusicPlayer playtrack={this.state.audio} songname={this.state.songname}/>
            </div>
            {
            console.log(jsondata[this.state.i].this.state.property)
            }
            </section>
        )
      }
}

export default Playlistpage
