import React, {Component} from 'react'


class Form extends Component {
constructor(){
    super()
    this.state = {
        firstname:"",
        lastname:""
    }
}

getformData = (e) => {
    this.setState({
        [e.target.name]:e.target.value
    })
}

sendDatatoServer = () => {
    fetch("http://localhost:4000/",{
        method:"post",
        body:JSON.stringify(this.state.firstname),
    }).then(data => console.log(data))
}

    render(){
    return (
        <form>
            <input type="text" name="firstname" onChange={this.getformData} value={this.state.firstname}/>
            <input type="text" name="lastname" onChange={this.getformData} value={this.state.lastname}/>
            <input type="submit" value="submit" onClick={this.sendDatatoServer}/>
            {this.state.firstname}
            {this.state.lastname}
        </form>
    )
}
}

export default Form
