import React from "react";
import {Component} from "react"
import jsondata from "./data.json";
import songsdataarray from "./importsongs"

class MusicPlayer extends Component{
    constructor(props){
        super(props)
        this.state = {
            audio:{"Matargashti - Tamasha":songsdataarray[0],
            "Kahani":songsdataarray[1],
            "Tera Fitoor":songsdataarray[2],
            "Kurta Suha":songsdataarray[3], 
          "Jeena Jeena":songsdataarray[4],
          "Channa Mereya":songsdataarray[5],
          "Surrender- Natalie Taylor":songsdataarray[6],
          "Humnava Mere":songsdataarray[7],
          "Ki Honda Pyaar":songsdataarray[8],
        }
        }
    }

Updatesrc = () => {
    let audio = document.querySelector("audio")
    audio.src = this.state.audio[Object.keys(this.state.audio)[Object.keys(this.state.audio).indexOf(this.props.songname)]]

}
componentDidMount(){
    this.Updatesrc()
}

componentDidUpdate(){
    this.Updatesrc()
}

    render(){
        return (
            <div>
            <section>
                 <p>{this.props.songname}</p>
            <audio name="autonext" onEnded={(e)=>{this.props.changesong(e)}} autoPlay={true} controls>
              <source type="audio/mp3" ></source>
            </audio>
            </section>
          
            </div>
        )
    }
}   

export default MusicPlayer
