import React from "react";
import {Component} from "react"
import Matargashti from "../audio/Matargashti - Tamasha.mp3"
import Surrender from "../audio/Surrender- Natalie Taylor.mp3"
import Kahani from "../audio/Kahani.mp3"
import TeraFitoor from "../audio/Tera Fitoor.mp3"
import KurtaSuha from "../audio/Kurta Suha.mp3"
import JeenaJeena from "../audio/Jeena Jeena.mp3"
import HumnavaMere from "../audio/Humnava Mere.mp3"


class MusicPlayer extends Component{
    constructor(props){
        super(props)
        this.state = {
            audio:[Matargashti, Kahani, Surrender, TeraFitoor, KurtaSuha, JeenaJeena, HumnavaMere],
        }
    }

Updatesrc = () => {
    let audio = document.querySelector("audio")
    audio.src = this.state.audio[this.props.playtrack]
}
componentDidMount(){
    this.Updatesrc()
}

componentDidUpdate(){
    this.Updatesrc()
}

    render(){
        return (
            <div>
            <section>
                 <p>{this.props.songname}</p>
            <audio autoPlay={true} controls>
              <source type="audio/mp3" ></source>
            </audio>
            <p>
            <a href="#">Prev</a>
            <a href="#">Next</a>
            </p>
            </section>
            </div>
        )
    }
}   

export default MusicPlayer
