import logo from "./images/logo.png"
import './App.css';
import React from "react"
import Playlistpage from "./Components/playlistpage"
import Homepage from "./Components/Homepage"
import Form from "./Components/form"


class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      apiresponse:[],
      currentpage:"",
      songslist:[]
    }
  }

  openplaylist = (e) => {
    this.setState({
      currentpage:e.target.className
    })
  }

  ApiCall = () => {
    fetch("http://localhost:4000/test")
    .then(res => res.json())
    .then(response => {
      this.setState({
        apiresponse:response
      })
    })

  }
componentDidMount(){
  this.ApiCall()
}


render() {

  return (
    <div>
      <header>
      <ul>
        <li><img src={logo} style={{"height":"50px","width":"50px"}}/></li>
        <li>HOME</li>
        <li>ABOUT US</li>
        <li>PRICING</li>
      </ul>
      <Form/>
     {
      this.state.currentpage !== ""?<Playlistpage shoplaylist={this.state.currentpage}/>:<Homepage selected={this.openplaylist}/>
     }
    {this.state.apiresponse.map((data,i)=>{
      return (
        <ul>
        <li><input type="checkbox" value={data[0]} onClick={(e)=>this.state.songslist.push("value")}/>{data[0]}</li>
        </ul>
      )})}  
      {console.log(this.state.songslist)}
      </header>
    </div>
  );
}
}

export default App;
