import React from "react";
import {Component} from "react"
import jsondata from "./data.json";
import {songslistobject} from "./importsongs"
import Matargashti from "../audio/Matargashti - Tamasha.mp3"
import Surrender from "../audio/Surrender- Natalie Taylor.mp3"
import Kahani from "../audio/Kahani.mp3"
import TeraFitoor from "../audio/Tera Fitoor.mp3"
import KurtaSuha from "../audio/Kurta Suha.mp3"
import JeenaJeena from "../audio/Jeena Jeena.mp3"
import HumnavaMere from "../audio/Humnava Mere.mp3"
import kiHondaPyar from "../audio/Ki Honda Pyaar.mp3"
import channaMereya from "../audio/Channa Mereya (DjRaag.Net).mp3"
import Pal_MonsoonShootout from "../audio/Pal-Monsoon Shootout.mp3"
import SanamRe from "../audio/Sanam Re.mp3"
import Pal_Jalebi from "../audio/Pal-Jalebi.mp3"
import Raabta from "../audio/Raabta.mp3"

class MusicPlayer extends Component{
    constructor(props){
        super(props)
        this.state = {
            audio:songslistobject
        }
    }

Updatesrc = () => {
    let audio = document.querySelector("audio")
    audio.src = this.state.audio[Object.keys(this.state.audio)[Object.keys(this.state.audio).indexOf(this.props.songname)]]

}
componentDidMount(){
    this.Updatesrc()
}

componentDidUpdate(){
    this.Updatesrc()
}

    render(){
        return (
            <div>
            <section>
                 <p>{this.props.songname}</p>
            <audio name="autonext" onEnded={(e)=>{this.props.changesong(e)}} autoPlay={true} controls>
              <source type="audio/mp3" ></source>
            </audio>
            </section>
         {
             console.log(songslistobject)
         }
            </div>
        )
    }
}   

export default MusicPlayer
