import React, {Component} from 'react'
import axios from "axios"

class Form extends Component {
constructor(){
    super()
    this.state = {
        firstname:"",
        lastname:""
    }
}

getformData = (e) => {
    this.setState({
        [e.target.name]:e.target.value
    })
}

sendDatatoServer = (e) => {
      e.preventDefault()
      fetch("http://localhost:4000/test",{
          method:"POST",
          header:{"content-type":"application/json"},
          body:JSON.stringify(this.state)
      })
      .then(data => data.json()).then(response => console.log(response))
}

    render(){
    return (
        <form>
            <input type="text" name="firstname" onChange={this.getformData} value={this.state.firstname}/>
            <input type="text" name="lastname" onChange={this.getformData} value={this.state.lastname}/>
            <input type="submit" value="submit" onClick={this.sendDatatoServer}/>
            
        </form>
    )
}
}

export default Form
